and ("sign_id"=#{signId} or "sign_id" is null)
and "sign_id"=#{signId}

select "id" from
${TBSNAME}."mw_ipaddressmanage_table"
where "type" = 'iPaddresses' and "timing"=#{time}

SELECT * FROM ( SELECT "id", 1 AS "IPv4", "index_sort" FROM "mw_ipaddressmanage_table" a1 WHERE a1."parent_id" = #{parentId}
and a1."id" !=#{id}
UNION ALL SELECT "id", 0 AS "IPv6", "index_sort" FROM "mw_ipv6manage_table" a1 WHERE a1."parent_id" = #{parentId}
and a1."id" !=#{id}
) a2 ORDER BY a2."index_sort" DESC

select "ip_address"
from "mw_ipaddressmanagelist_table"
where "link_id" = #{id}

